export function getWordsByLevel(level: CEFRLevel): VocabWord[] {
  switch (level) {
    case 'A1': return VOCAB_A1;
    case 'A2': return VOCAB_A2;
    case 'B1': return VOCAB_B1;
    case 'B2': return VOCAB_B2;
    case 'C1': return VOCAB_C1;
    case 'C2': return VOCAB_C2;
    default: return [];
  }
}

